def countdown(n):
    while n > 0:
        yield n
        n -= 1

for i in countdown(3):
    print(i)

def make_adder(n):
    def add(x):
        return x + n
    return add

add5 = make_adder(5)
print(add5(3))

def gen(n):
    for i in range(n):
        yield i

print(sum(gen(3)))

def f(items):
    return [x for x in items if x % 2 == 0]

print(f([1,2,3,4]))

def deco(f):
    def wrapped(*args, **kwargs):
        return f(*args, **kwargs) + 1
    return wrapped

@deco
def base():
    return 1

print(base())
