import math
class Circle:
    def __init__(self, r):
        self.r = r
    def area(self):
        return math.pi * self.r * self.r

print(round(Circle(2).area(), 2))

def run(xs):
    out = []
    for x in xs:
        try:
            if x % 2 == 0:
                out.append(x)
            else:
                raise ValueError('odd')
        except Error as e:
            print('Error')
    return out

print(run([1,2,3,4,5]))

class Bag:
    def __init__(self):
        self.items = []
    def add(self, x):
        self.items.append(x)
    def size(self):
        return len(self.items)

b = Bag()
b.add('a'); b.add('b')
print(b.size())

