class Dog:
    def __init__(self, name):
        self.name = name

    def speak(self):
        return f"{self.name} says Woof!"

dog = Dog("Buddy")
print(dog.speak())

class Animal:
    def __init__(self):
        self.sound = "generic"

class Cat(Animal):
    def __init__(self):
        super().__init__()
        self.sound = "meow"

cat = Cat()
print(cat.sound)

class A:
    def f(self):
        return 'A'
class B(A):
    def f(self):
        return 'B'
print(B().f())

class P:
    def __init__(self,x):
        self.x = x
    def __repr__(self):
        return f'P({self.x})'
print(P(3))

class V:
    def __init__(self):
        self._x = 1
    @property
    def x(self):
        return self._x
print(V().x)

class Base:
    def __init__(self):
        self.v = 1
class Child(Base):
    def __init__(self):
        super().__init__()
        self.v += 1
print(Child().v)
